mapper.select:
   SELECT product.product_no, name, price, main_img, detail_no, img , content
FROM product,product_detail
WHERE product.product_no=product_detail.product_no AND product.product_no=#{no}

mapper.insert:
INSERT INTO product_keep VALUES(
  (SELECT NVL(MAX(product_no)+1,1) FROM product_keep),#{mem_id},#{pno}
)

mapper.select:
SELECT * FROM product_keep
WHERE mem_id=#{mem_id}
ORDER BY product_no DESC

mapper.insert:
INSERT INTO like_product VALUES(
  (SELECT NVL(MAX(product_no)+1,1) FROM like_product),#{mem_id},#{cno}
)

mapper.select:
SELECT * FROM like_product
WHERE mem_id=#{mem_id}
ORDER BY product_no DESC
