mapper.select:
SELECT rest_review_no,rest_review_bno,id,name,rest_review_content,
group_tab FROM restaurant_review
WHERE rest_review_bno=#{rest_review_bno}

mapper.update:
UPDATE reservation SET
isreserve='1'
WHERE rest_no=#{rest_no}

mapper.insert:
INSERT INTO like_restaurant VALUES(
  (SELECT NVL(MAX(rest_no)+1,1) FROM like_restaurant),#{id},#{cno}
)

mapper.select:
SELECT * FROM like_restaurant
WHERE mem_id=#{mem_id}
ORDER BY rest_no DESC
